import React, { useEffect, useState } from 'react';

export default function App() {
  const [products, setProducts] = useState([]);
  const [results, setResults] = useState([]);
  const [query, setQuery] = useState('');
  const [category, setCategory] = useState('All');
  const [maxPrice, setMaxPrice] = useState('');

  useEffect(() => {
    fetch('/api/products')
      .then((r) => r.json())
      .then((data) => {
        setProducts(data);
        setResults(data);
      });
  }, []);

  const categories = ['All', ...Array.from(new Set(products.map((p) => p.category)))];

  function applyFilter() {
    let r = products.filter(
      (p) => (category === 'All' || p.category === category) && (maxPrice === '' || p.price <= parseFloat(maxPrice))
    );
    setResults(r);
  }

  async function aiSearch(e) {
    e && e.preventDefault();
    const resp = await fetch('/api/search', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ query }),
    });
    const json = await resp.json();
    if (json.ids && json.ids.length > 0) {
      setResults(products.filter((p) => json.ids.includes(p.id)));
    } else {
      setResults(products);
    }
  }

  return (
    <div className="container">
      <h1>AI Product Catalog</h1>

      <form onSubmit={aiSearch} className="row">
        <input
          className="input"
          placeholder='Search: "running shoes under $100"'
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
        <button type="submit">AI Search</button>
      </form>

      <div className="filters">
        <select value={category} onChange={(e) => setCategory(e.target.value)}>
          {categories.map((c) => (
            <option key={c}>{c}</option>
          ))}
        </select>
        <input
          type="number"
          placeholder="Max price"
          value={maxPrice}
          onChange={(e) => setMaxPrice(e.target.value)}
        />
        <button onClick={applyFilter}>Apply Filter</button>
      </div>

      <ul className="products">
        {results.map((p) => (
          <li key={p.id}>
            <b>{p.name}</b> (${p.price.toFixed(2)}) <i>{p.category}</i> - Rating: {p.rating} <br />
            {p.description}
          </li>
        ))}
      </ul>
    </div>
  );
}
