// App.jsx
import React, { useEffect, useState } from "react";

export default function App() {
  const [products, setProducts] = useState([]);
  const [results, setResults] = useState([]);
  const [query, setQuery] = useState("");
  const [category, setCategory] = useState("All");
  const [maxPrice, setMaxPrice] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  useEffect(() => {
    // Load products from your products.json file
    fetch("/products.json")
      .then((r) => {
        if (!r.ok) {
          throw new Error("Failed to fetch products");
        }
        return r.json();
      })
      .then((data) => {
        setProducts(data);
        setResults(data);
      })
      .catch((err) => {
        console.error("Error loading products:", err);
        setError("Failed to load products. Please try again later.");
      });
  }, []);

  const categories = ["All", ...Array.from(new Set(products.map((p) => p.category)))];

  function applyFilter() {
    setError("");
    const filtered = products.filter(
      (p) =>
        (category === "All" || p.category === category) &&
        (maxPrice === "" || p.price <= parseFloat(maxPrice))
    );
    setResults(filtered);
    if (filtered.length === 0) {
      setError("No products match the filter criteria.");
    }
  }

  async function aiSearch() {
    if (!query.trim()) return;

    setError("");
    setLoading(true);

    try {
      // Try to use your existing API endpoint first
      const resp = await fetch("/api/search", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ query }),
      });

      if (resp.ok) {
        const json = await resp.json();
        if (json.ids && json.ids.length > 0) {
          setResults(products.filter((p) => json.ids.includes(p.id)));
        } else {
          setResults([]);
          setError("No products found matching your AI search.");
        }
      } else {
        throw new Error("API search failed");
      }
    } catch (apiError) {
      console.log("API search failed, falling back to local search");

      // Fallback to local search
      const searchResults = products.filter(p => {
        const searchTerm = query.toLowerCase();
        return (
          p.name.toLowerCase().includes(searchTerm) ||
          p.description.toLowerCase().includes(searchTerm) ||
          p.category.toLowerCase().includes(searchTerm) ||
          (p.tags && p.tags.some(tag => tag.toLowerCase().includes(searchTerm)))
        );
      });

      setResults(searchResults);
      if (searchResults.length === 0) {
        setError("No products found matching your search.");
      }
    } finally {
      setLoading(false);
    }
  }

  const resetFilters = () => {
    setQuery("");
    setCategory("All");
    setMaxPrice("");
    setResults(products);
    setError("");
  };

  const getCategoryEmoji = (category) => {
    const emojiMap = {
      "Electronics": "üì±",
      "Footwear": "üëü",
      "Kitchen": "‚òï",
      "Fitness": "üßò",
      "Clothing": "üëï",
      "Books": "üìö",
      "Home": "üè†",
      "Beauty": "üíÑ",
      "Sports": "‚öΩ",
      "Toys": "üß∏",
      "Default": "üì¶"
    };
    return emojiMap[category] || emojiMap["Default"];
  };

  const renderStars = (rating) => {
    const fullStars = Math.floor(rating);
    const hasHalfStar = rating % 1 !== 0;

    return (
      <span className="rating-stars">
        {"‚òÖ".repeat(fullStars)}
        {hasHalfStar && "‚òÜ"}
      </span>
    );
  };

  return (
    <div className="app-container">
      {/* Header */}
      <div className="header">
        <div className="header-content">
          <h1 className="main-title">ü§ñ AI Product Catalog</h1>
        </div>
      </div>

      {/* Main Content */}
      <div className="main-content">
        {/* Search Section */}
        <div className="search-section">
          <div className="search-form">
            <div className="search-input-wrapper">
              <input
                className="search-input"
                placeholder='üîç Search: "running shoes under $100"'
                value={query}
                onChange={(e) => setQuery(e.target.value)}
                disabled={loading}
                onKeyPress={(e) => e.key === 'Enter' && aiSearch()}
              />
            </div>
            <button
              onClick={aiSearch}
              disabled={loading || !query.trim()}
              className="btn-primary"
            >
              {loading ? (
                <span className="loading-content">
                  <span className="loading-spinner"></span>
                  Searching...
                </span>
              ) : (
                "‚ú® AI Search"
              )}
            </button>
          </div>

          {/* Filters */}
          <div className="filters">
            <select
              className="filter-select"
              value={category}
              onChange={(e) => setCategory(e.target.value)}
              disabled={loading}
            >
              {categories.map((c) => (
                <option key={c} value={c}>
                  {c === "All" ? "üè∑Ô∏è All Categories" : `${getCategoryEmoji(c)} ${c}`}
                </option>
              ))}
            </select>

            <input
              type="number"
              min="0"
              step="0.01"
              placeholder="üí∞ Max price"
              value={maxPrice}
              onChange={(e) => setMaxPrice(e.target.value)}
              disabled={loading}
              className="filter-input"
            />

            <button
              onClick={applyFilter}
              disabled={loading}
              className="btn-secondary"
            >
              üîß Apply Filter
            </button>

            <button
              onClick={resetFilters}
              disabled={loading}
              className="btn-reset"
            >
              üîÑ Reset
            </button>
          </div>
        </div>

        {/* Error Message */}
        {error && (
          <div className="error-message">
            <span>‚ö†Ô∏è</span>
            <span>{error}</span>
          </div>
        )}

        {/* Results Count */}
        <div className="results-count">
          Showing <span className="results-number">{results.length}</span> product{results.length !== 1 ? 's' : ''}
        </div>

        {/* Products Grid */}
        <div className="products-grid">
          {results.map((p) => (
            <div key={p.id} className="product-card">
              {/* Product Image Placeholder */}
              <div className="product-image-placeholder">
                {getCategoryEmoji(p.category)}
              </div>

              {/* Product Info */}
              <div className="product-info">
                <h3 className="product-title">{p.name}</h3>

                <div className="product-header">
                  <div className="product-price">${p.price.toFixed(2)}</div>
                  <div className="product-category">{p.category}</div>
                </div>

                <p className="product-description">{p.description}</p>

                <div className="product-rating">
                  {renderStars(p.rating)}
                  <span className="rating-text">({p.rating})</span>
                </div>

                <button className="btn-cart">
                  Add to Cart üõí
                </button>
              </div>
            </div>
          ))}
        </div>

        {/* Empty State */}
        {results.length === 0 && !error && !loading && (
          <div className="empty-state">
            <div className="empty-icon">üîç</div>
            <h3 className="empty-title">No products found</h3>
            <p className="empty-description">Try adjusting your search or filters</p>
          </div>
        )}
      </div>
    </div>
  );
}
